graphql.query:
mutation applyBundle (
    $clusterProperties: [ClusterPropertyInput!]!=[],
    $webApiServices: [WebApiServiceInput!]!=[],
    $encassConfigs:[EncassConfigInput!]!=[],
    $revocationCheckPolicies:[RevocationCheckPolicyInput!]!=[],
    $trustedCerts:[TrustedCertInput!]!=[],
    $dtds:[DtdInput!]!=[],
    $schemas:[SchemaInput!]!=[],
    $jdbcConnections:[JdbcConnectionInput!]!=[],
    $soapServices:[SoapServiceInput!]!=[],
    $policyFragments: [PolicyFragmentInput!]!=[],
    $fips:[FipInput!]!=[],
    $ldaps:[LdapInput!]!=[],
    $fipGroups:[FipGroupInput!]!=[],
    $internalGroups:[InternalGroupInput!]!=[],
    $fipUsers:[FipUserInput!]!=[],
    $internalUsers:[InternalUserInput!]!=[],
    $keys: [KeyInput!]!=[],
    $secrets: [SecretInput!]!=[],
    $cassandraConnections:[CassandraConnectionInput!]!=[],
    $jmsDestinations:[JmsDestinationInput!]!=[],
    $emailListeners: [EmailListenerInput!]!=[],
    $listenPorts: [ListenPortInput!]!=[],
    $activeConnectors: [ActiveConnectorInput!]!=[],
    $smConfigs: [SMConfigInput!]!=[],
    $globalPolicies:[GlobalPolicyInput!]!=[],
    $backgroundTaskPolicies:[BackgroundTaskPolicyInput!]!=[],
    $scheduledTasks:[ScheduledTaskInput!]!=[],
    $internalWebApiServices: [WebApiServiceInput!]!=[],
    $internalSoapServices:[SoapServiceInput!]!=[],
    $serverModuleFiles:[ServerModuleFileInput!]!=[],
    $passwordPolicies:[PasswordPolicyInput!]!=[],
    $serviceResolutionConfigs:[ServiceResolutionConfigInput!]!=[],
    $administrativeUserAccountProperties: [AdministrativeUserAccountPropertyInput!]!=[]) {
    setServerModuleFiles(input: $serverModuleFiles){serverModuleFiles{goid} status}
    setClusterProperties (input: $clusterProperties){clusterProperties {goid} status}
    setServiceResolutionConfigs (input: $serviceResolutionConfigs){serviceResolutionConfigs {goid} status}
    setPasswordPolicies (input: $passwordPolicies){passwordPolicies {goid} status}
    setAdministrativeUserAccountProperties (input: $administrativeUserAccountProperties){administrativeUserAccountProperties {goid} status}

    setRevocationCheckPolicies (input: $revocationCheckPolicies){revocationCheckPolicies {goid} status}
    setTrustedCerts (input: $trustedCerts){trustedCerts {goid} status}
    setSecrets (input: $secrets){secrets{name} status}
    setSchemas (input: $schemas){schemas {goid} status}
    setDtds (input: $dtds){dtds {goid} status}
    setJdbcConnections (input: $jdbcConnections){jdbcConnections {goid} status}
    setFips (input: $fips){fips{goid} status}
    setLdaps (input: $ldaps){ldaps{goid} status}
    setFipGroups (input: $fipGroups){fipGroups{goid} status}
    setInternalGroups (input: $internalGroups){internalGroups{goid} status}
    setFipUsers (input: $fipUsers){fipUsers{goid} status}
    setInternalUsers (input: $internalUsers){internalUsers{goid} status}
    setCassandraConnections (input: $cassandraConnections){cassandraConnections {goid} status}
    setJmsDestinations (input: $jmsDestinations){jmsDestinations {goid} status}
    setSMConfigs (input: $smConfigs){smConfigs {goid} status}
    setPolicyFragments (input: $policyFragments){policyFragments {goid} status}
    setEncassConfigs (input: $encassConfigs){encassConfigs {goid} status}
    setGlobalPolicies (input: $globalPolicies){globalPolicies {goid} status}
    setBackgroundTaskPolicies (input: $backgroundTaskPolicies){backgroundTaskPolicies {goid} status}
    setWebApiServices (input: $webApiServices){webApiServices {goid} status}
    setSoapServices (input: $soapServices){soapServices {goid} status}
    setInternalWebApiServices (input: $internalWebApiServices){internalWebApiServices {goid} status}
    setInternalSoapServices (input: $internalSoapServices){internalSoapServices {goid} status}
    setEmailListeners (input: $emailListeners){emailListeners {goid} status}
    setListenPorts (input: $listenPorts){listenPorts {goid} status}
    setActiveConnectors (input: $activeConnectors){activeConnectors {goid} status}
    setScheduledTasks (input: $scheduledTasks){scheduledTasks {goid} status}

    # Keys must be mutated at the end
    setKeys (input: $keys) {keys {alias} status}
}

query policyFragmentByName ($name: String!) {
    policyFragmentByName (name: $name) {
        goid
        guid
        name
        checksum

        folderPath
        soap
        policy {
            xml
            allDependencies {
                encassConfigs {
                    goid guid name checksum 
                    description policyName 
                    encassArgs { name type ordinal guiPrompt guiLabel } 
                    encassResults{ name type } 
                    properties { name value }
                }
                policyFragments {
                    goid guid name checksum 
                    folderPath soap 
                    policy { xml }
                }

                activeConnectors {
                    goid name checksum 
                    enabled connectorType hardwiredServiceName 
                    properties { name value } 
                    advancedProperties { name value }
                }
                dtds {
                    goid systemId checksum 
                    publicId description content
                }
                cassandraConnections {
                    goid name checksum 
                    enabled keyspace contactPoints port compression username securePasswordName sslEnabled cipherSuites 
                    properties { name value }
                }
                clusterProperties {
                    goid name checksum 
                    description hiddenProperty value
                }
                emailListeners {
                    goid name checksum 
                    enabled serverType hostname port folder deleteOnReceive username password hardwiredServiceName sslEnabled pollInterval sizeLimit 
                    properties { name value }
                }
                fips {
                    goid name checksum 
                    enableCredentialTypeSaml enableCredentialTypeX509 certificateValidation 
                    certificateReferences { 
                        goid name subjectDn thumbprintSha1 checksum 
                        verifyHostname trustAnchor trustedFor revocationCheckPolicyType 
                        revocationCheckPolicy { goid name } 
                        notBefore notAfter certBase64 
                    }
                }
                fipGroups {
                    goid name providerName checksum 
                    description 
                    members { 
                        goid name login providerName checksum 
                        subjectDn certBase64 firstName lastName email 
                    }
                }
                fipUsers {
                    goid name login providerName checksum 
                    subjectDn certBase64 firstName lastName email 
                    memberOf { 
                        goid name providerName checksum 
                        description 
                    }
                }
                internalGroups {
                    goid name checksum 
                    description 
                    members { 
                        goid name login checksum 
                        enabled password certBase64 firstName lastName email 
                    }
                }
                internalUsers {
                    goid name login checksum 
                    enabled password certBase64 firstName lastName email 
                    memberOf { goid name checksum description }
                }
                jdbcConnections {
                    goid name checksum 
                    enabled driverClass jdbcUrl username password minPoolSize maxPoolSize 
                    properties { name value }
                }
                jmsDestinations {
                    goid connectionGoid name direction providerType checksum 
                    enabled template initialContextFactoryClassname connectionFactoryName jndiUrl jndiUsername jndiPassword 
                    jndiSslDetails { sslEnabled sslForAuthenticationOnly sslVerifyServerCertificate sslVerifyServerHostname sslClientKeyAlias } 
                    destinationType destinationName destinationUsername destinationPassword 
                    destinationSslDetails { sslEnabled sslForAuthenticationOnly sslVerifyServerCertificate sslVerifyServerHostname sslClientKeyAlias } 
                    properties { name value }
                }
                keys {
                    goid keystoreId alias checksum 
                    keyType subjectDn p12 certChain
                }
                ldaps {
                    goid name checksum 
                    ldapUrls ldapsClientAuthEnabled ldapsClientKeystoreId ldapsClientKeyAlias searchBase writable bindDn bindPassword 
                    userMappings { objClass nameAttrName loginAttrName passwdAttrName firstNameAttrName lastNameAttrName emailNameAttrName kerberosAttrName kerberosEnterpriseAttrName userCertAttrName passwdType { val } } 
                    groupMappings { objClass nameAttrName memberAttrName memberStrategy { val } }
                }
                listenPorts {
                    goid name checksum 
                    enabled protocol port hardwiredServiceName enabledFeatures 
                    tlsSettings { clientAuthentication keystoreId keyAlias tlsVersions cipherSuites useCipherSuitesOrder } 
                    properties { name value }
                }
                schemas {
                    goid systemId checksum 
                    targetNs description content
                }
                secrets {
                    goid name checksum 
                    description secret secretType variableReferencable
                }
                serverModuleFiles {
                    goid name checksum 
                    moduleType moduleSha256 signature signerCertBase64 
                    properties { name value } 
                    moduleStates { nodeId nodeName state description } 
                    moduleStateSummary { state description }
                }
                smConfigs {
                    goid name checksum 
                    enabled agentHost agentIP agentHostConfig agentSecret cryptoMode ipCheckEnabled updateSSOToken clusterFailoverThreshold nonClusterFailover username securePasswordName 
                    properties { name value }
                }
                revocationCheckPolicies {
                goid name checksum defaultPolicy defaultSuccess
                continueOnServerUnavailable
                    revocationCheckPolicyItems {
                        ... on RevocationCheckPolicyItem {
                        type url allowIssuerSignature
                        nonceUsage signerThumbprintSha1s
                        }
                    }
                }
                trustedCerts {
                    goid name subjectDn thumbprintSha1 checksum 
                    verifyHostname trustAnchor trustedFor revocationCheckPolicyType 
                    revocationCheckPolicy { goid name } 
                    notBefore notAfter certBase64
                }
            }
        }
    }
}

