query folderContents ($folderPath: String!) {
    webApiServicesByFolderPath (folderPath: $folderPath) {
        goid
        name
        resolutionPath
        checksum

        enabled
        folderPath
        methodsAllowed
        properties {name value}
        tracingEnabled
        wssProcessingEnabled

        policy {xml}
    }

    soapServicesByFolderPath (folderPath: $folderPath) {
        goid 
        name 
        resolutionPath 
        resolvers { soapActions baseUri resolutionPath } 
        checksum 

        enabled 
        folderPath 
        soapVersion 
        methodsAllowed 
        tracingEnabled 
        wssProcessingEnabled 
        laxResolution 
        properties { name value } 
        wsdl 
        policy { xml }
    }

    internalWebApiServicesByFolderPath (folderPath: $folderPath) {
        goid
        name
        resolutionPath
        checksum

        enabled
        folderPath
        methodsAllowed
        properties {name value}
        tracingEnabled
        wssProcessingEnabled

        policy {xml}
    }

    internalSoapServicesByFolderPath (folderPath: $folderPath) {
        goid 
        name 
        resolutionPath 
        resolvers { soapActions baseUri resolutionPath } 
        checksum 

        enabled 
        folderPath 
        soapVersion 
        methodsAllowed 
        tracingEnabled 
        wssProcessingEnabled 
        laxResolution 
        properties { name value } 
        wsdl 
        policy { xml }
    }

    policyFragmentsByFolderPath (folderPath: $folderPath) {
        goid 
        guid 
        name 
        checksum 

        folderPath 
        soap
        policy { xml }
    }

    globalPoliciesByFolderPath (folderPath: $folderPath) {
        goid 
        guid 
        name 
        tag 
        checksum 

        folderPath 
        policy { xml }
    }

    backgroundTaskPoliciesByFolderPath (folderPath: $folderPath) {
        goid 
        guid 
        name 
        checksum 

        folderPath 
        soap 
        policy { xml }
    }

}

