    administrativeUserAccountProperties {
        goid name value checksum
    }
    serviceResolutionConfigs {
        goid checksum
        resolutionPathRequired resolutionPathCaseSensitive
        useL7OriginalUrl useServiceGoid
        useSoapAction useSoapBodyChildNamespace        
    }
}

