mutation setActiveConnectors (
    $activeConnectors: [ActiveConnectorInput!]!,
    $secrets: [SecretInput!]!,
    $webApiServices: [WebApiServiceInput!]!) {
    setSecrets (input: $secrets) {
        detailedStatus {status description}
        secrets {
            goid
            name
            checksum
        }
    }
    setWebApiServices(input: $webApiServices) {
        detailedStatus {status description}
        webApiServices {
            goid
            name
            resolutionPath
            checksum
        }
    }
    setActiveConnectors (input: $activeConnectors) {
        detailedStatus {status description}
        activeConnectors {
            goid
            name
            checksum
        }
    }
}

