mutation deleteJmsDestinations($names: [String!]!) {
    deleteJmsDestinations(names: $names) {
        detailedStatus {status description}
        jmsDestinations {
            goid
            connectionGoid
            name
            direction
            providerType
        }
    }
}

query listenPorts {
    listenPorts {
        goid 
        name 
        checksum 

        enabled 
        protocol 
        port 
        hardwiredServiceName 
        enabledFeatures 
        tlsSettings { 
            clientAuthentication 
            keystoreId 
            keyAlias 
            tlsVersions 
            cipherSuites 
            useCipherSuitesOrder 
        } 
        properties { name value }

        hardwiredService {
            ... on PublishedService {
                name 
                resolutionPath 
                folderPath 
                methodsAllowed 
                enabled
            }
        }
    }
}

query listenPortByName ($name : String!) {
    listenPortByName (name : $name) {
        goid 
        name 
        checksum 

        enabled 
        protocol 
        port 
        hardwiredServiceName 
        enabledFeatures 
        tlsSettings { 
            clientAuthentication 
            keystoreId 
            keyAlias 
            tlsVersions 
            cipherSuites 
            useCipherSuitesOrder 
        } 
        properties { name value }

        hardwiredService {
            ... on PublishedService {
                name 
                resolutionPath 
                folderPath 
                methodsAllowed 
                enabled
            }
        }
    }
}

query listenPortsByProtocol ($protocol : String!) {
    listenPortsByProtocol (protocol : $protocol) {
        goid 
        name 
        checksum 

        enabled 
        protocol 
        port 
        hardwiredServiceName 
        enabledFeatures 
        tlsSettings { 
            clientAuthentication 
            keystoreId 
            keyAlias 
            tlsVersions 
            cipherSuites 
            useCipherSuitesOrder 
        } 
        properties { name value }

        hardwiredService {
            ... on PublishedService {
                name 
                resolutionPath 
                folderPath 
                methodsAllowed 
                enabled
            }
        }
    }
}

mutation setListenPorts (
    $listenPorts: [ListenPortInput!]!, 
    $webApiServices: [WebApiServiceInput!]!) {
    # Ensure the availability of listen port dependencies
    setWebApiServices(input: $webApiServices) {
        detailedStatus {status description}
        webApiServices {
            goid
            name
            resolutionPath
            checksum
        }
    }
    setListenPorts (input: $listenPorts) {
        detailedStatus {status description}
        listenPorts {
            goid 
            name 
            checksum 
        }
    }
}

